import React from 'react';
//import Heading from './Heading';
// import Para from './Para';
// import List from './List';
import * as Calc from './Calc';

function App(){

    // let curDate = new Date(2021,8,18,13);
    let curDate = new Date();
    curDate = curDate.getHours();
    let greeting = "";
    const cssStyle = { };
    const myName = "Rajendra";

  if(curDate >= 1 && curDate < 12){
    greeting = "Good Morning.";
    cssStyle.color = 'green';
  }else if(curDate >= 12 && curDate < 19){
    greeting = "Good Afternoon.";
    cssStyle.color = 'Orange';
  }else{
    greeting = "Good Night.";
    cssStyle.color = 'black';
  }


    return (
        <>
            <div>
                 <h1>Hello {myName}, <span style={cssStyle}>{greeting}</span></h1>
                 <ol>
                     <li>Sum : {Calc.add(30,60)}</li>
                     <li>Division : {Calc.divide(10,3).toFixed(2)}</li>
                     <li>Substract : {Calc.minus(70,10)}</li>
                     <li>Multiply : {Calc.mult(30,60)}</li>
                 </ol>
            </div>
        </>
    );
}

export default App;
